package PrimerTrimestre;

public class Ejercicio2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		short x=7;
		int y=5;
		float f1=13.5f;
		float f2=8f;
		System.out.printf("El valor de x es %d y el valor de y es %d\n", x,y);
		System.out.println("El reslutado de x+y es: "+(x+y));
		System.out.println("El resultado de x-y es: "+(x-y));
		System.out.println("División entera: x/y= "+ (x/y));
		System.out.println("Resto de la división entera x%y= "+(x%y));
		System.out.printf("El valor de f1 es %f1 y el valor de f2 es %f\n",f1,f2);
		System.out.println("El resultado de f1/f2 es: "+(f1/f2));
	}

}
